CREATE TABLE Rating (id bigint(20) NOT NULL AUTO_INCREMENT, userId bigint(20) DEFAULT NULL, serviceId bigint(20) DEFAULT NULL, versionId bigint(20) DEFAULT NULL, rate int DEFAULT NULL, PRIMARY KEY (ratingId), FOREIGN KEY(serviceId) REFERENCES ClimateService(id), FOREIGN KEY(userId) REFERENCES User(id), FOREIGN KEY(versionId) REFERENCES Version(id));